import { Stack, TextField, Button } from "@mui/material";
import { Box } from "@mui/system";
import React, { useState } from "react";
import Cards from "react-credit-cards";
import "react-credit-cards/es/styles-compiled.css";
import CustomCenterBox from "./CustomCenterBox";
import { CardForm } from "react-payment";

function CardInputForm({ history }) {
  const [number, setNumber] = useState("");
  const [name, setName] = useState("");
  const [month, setMonth] = useState("");
  const [expiry, setExpiry] = useState("");
  const [cvc, setCvc] = useState("");
  const [focus, setFocus] = useState("");

  return (
    <>
      <Box sx={{ pt: 8 }}>
        <Cards
          locale={{ valid: "유효기간" }}
          placeholders={{ name: "별칭" }}
          number={number}
          name={name}
          expiry={expiry}
          cvc={cvc}
          focused={focus}
        />
      </Box>

      <Box sx={{ p: 4 }}>
        {/* <form
          style={{
            backgroundColor: "white",
            padding: "20px",
            borderRadius: "15px",
          }}
        >
          <Stack spacing={3}>
            <TextField
              inputProps={{
                maxLength: 16,
              }}
              type="tel"
              label="카드 번호"
              value={number}
              pattern="[0-9]+"
              onChange={(e) => {
                setNumber(e.target.value);
              }}
              onFocus={(e) => setFocus(e.target.name)}
            />

            <TextField
              inputProps={{
                maxLength: 4,
              }}
              type="tel"
              label="유효기간"
              value={expiry}
              pattern="[0-9]+"
              placeholder="MM/YY"
              onChange={(e) => {
                setExpiry(e.target.value);
              }}
              onFocus={(e) => setFocus(e.target.name)}
            />

            <TextField
              inputProps={{
                maxLength: 3,
              }}
              type="tel"
              label="CVC"
              value={cvc}
              pattern="\d*"
              onChange={(e) => {
                setCvc(e.target.value);
              }}
              onFocus={(e) => setFocus(e.target.name)}
            />

            <TextField
              type="text"
              label="별칭"
              value={name}
              onChange={(e) => {
                setName(e.target.value);
              }}
              onFocus={(e) => setFocus(e.target.name)}
            />
          </Stack>
        </form> */}
        <CardForm onSubmit={this.onSubmit} getName={true} getZip={true} />
      </Box>

      <CustomCenterBox>
        <Button variant="contained">등록</Button>
        <Button
          variant="contained"
          onClick={() => {
            history.push("/");
          }}
        >
          취소
        </Button>
      </CustomCenterBox>
    </>
  );
}

export default CardInputForm;
