import React, { useState } from "react";

import { Button, Grid, TextField, Typography } from "@mui/material";
import { Box, styled } from "@mui/system";
import ArrowRightAltIcon from "@mui/icons-material/ArrowRightAlt";

import ContactUsImage from "../../assets/img/home/phone_battery1.png";

import { isMobile } from "react-device-detect";

import { Mailer } from "nodemailer-react";

const transport = {
  host: "smtp.gmail.com",
  secure: true,
  auth: { user: "sleeppturtle@gmail.com", pass: "ddngyxdwnotzagsx" },
};
export const WelcomeEmail = ({ firstName }) => ({
  subject: `👋 ${firstName}`,
  body: (
    <div>
      <p>Hello {firstName}!</p>
      <p>Hope you'll enjoy the package!</p>
    </div>
  ),
});
const mailer = Mailer(
  { transport, from: "sleeppturtle@gmail.com" },
  { WelcomeEmail, PasswordEmail, ReminderEmail }
);

const StyledTextField = styled(TextField)(({ theme }) => ({
  div: {
    borderRadius: 0,
    border: "0px",
  },
}));

function ContactUs() {
  const [name, setName] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [email, setEmail] = useState("");
  const [interested, setInterested] = useState("");
  const [message, setMessage] = useState("");

  const sendMail = () => {};

  return (
    <Grid
      container
      justifyContent="center"
      alignItems="center"
      p={4}
      sx={{ marginTop: { xs: 10, lg: 30 }, backgroundColor: "#E9E9EB" }}
    >
      <Grid item xs={12} lg={6}>
        <Box sx={{ pl: { lg: 7, xs: 0 }, pr: { lg: 4, xs: 0 } }}>
          <Typography
            fontSize={{ lg: 54, xs: 30 }}
            letterSpacing="0em"
            color="#BDBDBD"
            textAlign="left"
          >
            Contact Us
          </Typography>
          <StyledTextField
            fullWidth
            label="Name"
            margin="normal"
            variant="filled"
            value={name}
            onChange={(e) => {
              setName(e.target.value);
            }}
          />
          <StyledTextField
            fullWidth
            required
            label="Phone Number"
            type="number"
            margin="normal"
            variant="filled"
            value={phoneNumber}
            onChange={(e) => {
              setPhoneNumber(e.target.value);
            }}
          />
          <StyledTextField
            fullWidth
            required
            label="E-mail"
            type="email"
            margin="normal"
            variant="filled"
            value={email}
            onChange={(e) => {
              setEmail(e.target.value);
            }}
          />
          <StyledTextField
            fullWidth
            label="Interested In"
            margin="normal"
            variant="filled"
            value={interested}
            onChange={(e) => {
              setInterested(e.target.value);
            }}
          />
          <StyledTextField
            fullWidth
            required
            multiline
            rows={4}
            label="Message"
            margin="normal"
            variant="filled"
            value={message}
            onChange={(e) => {
              setMessage(e.target.value);
            }}
          />

          <Button
            sx={{
              color: "white",
              width: isMobile ? "150px" : "222px",
              height: isMobile ? "50px" : "71px",
              background: "#333333",
              borderRadius: 0,
            }}
            onClick={sendMail}
          >
            SEND EMAIL
            <ArrowRightAltIcon />
          </Button>
        </Box>
      </Grid>
      <Grid item xs={12} lg={6}>
        <img src={ContactUsImage} alt="" width="100%" />
      </Grid>
    </Grid>
  );
}

export default ContactUs;
