import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import { Typography } from "@mui/material";

export default function App(props) {
  const [stationId, setStationId] = React.useState("");

  function checkMobile() {
    var varUA = navigator.userAgent.toLowerCase(); //userAgent 값 얻기

    if (varUA.indexOf("android") > -1) {
      //안드로이드
      return "android";
    } else if (
      varUA.indexOf("iphone") > -1 ||
      varUA.indexOf("ipad") > -1 ||
      varUA.indexOf("ipod") > -1
    ) {
      //IOS
      return "ios";
    } else {
      //아이폰, 안드로이드 외
      return "other";
    }
  }

  React.useEffect(() => {
    console.log(checkMobile());
    if (props.location.state !== undefined) {
      setStationId(props.location.state.stationId);

      sessionStorage.setItem("stationId", props.location.state.stationId);
    }
  }, []);
  return (
    <>
      <div
        style={{
          backgroundColor: "#0B0B0C",
          height: "100vh",
          width: "100vw",
        }}
      >
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            paddingTop: "50px",
          }}
        >
          <Typography color="white" fontSize={40} fontWeight={700}>
            BANTO
          </Typography>
          <Typography color="white">반토 보조배터리 대여서비스</Typography>
        </div>

        <div
          style={{
            backgroundColor: "#0B0B0C",
            height: "80%",
            width: "100%",
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "space-evenly",
            marginTop: "14px",
          }}
        >
          <div
            style={{
              backgroundColor: "#0B0B0C",
              height: "80%",
              width: "100%",
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              marginTop: "25px",
            }}
          >
            <div
              style={{
                backgroundColor: "white",
                boxShadow: "0px 5px 14px #000000",
                width: "calc(100% - 64px)",
                height: "184px",
                borderRadius: "20px",
                display: "flex",
                flexDirection: "column",
              }}
              onClick={() => {
                props.history.push({
                  pathname: "/simple/login",
                  state: { stationId: stationId },
                });
              }}
            >
              <div
                style={{
                  marginLeft: "24px",
                  height: "100%",
                  display: "flex",
                  flexDirection: "column",
                }}
              >
                <p
                  style={{
                    marginTop: "32px",
                    fontSize: "26px",
                    fontWeight: "900",
                  }}
                >
                  간편 이용
                </p>
                <div
                  style={{
                    display: "flex",
                    flexDirection: "rows",
                    justifyContent: "space-between",
                    alignItems: "center",
                  }}
                >
                  <div style={{ marginTop: "4px" }}>
                    <p
                      style={{
                        lineHeight: "24px",
                        fontSize: "14px",
                      }}
                    >
                      앱 다운 없이 배터리를
                    </p>
                    <p
                      style={{
                        fontSize: "14px",
                      }}
                    >
                      대여할 수 있습니다
                    </p>
                  </div>
                </div>

                <div
                  style={{
                    marginTop: "20px",
                  }}
                >
                  <p
                    style={{
                      backgroundColor: "black",
                      color: "white",
                      borderRadius: "8px",
                      display: "inline",
                      padding: "4px 12px",
                      fontSize: "14px",
                      fontWeight: "600",
                    }}
                  >
                    결제수단 등록하러 가기
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
