import React from "react";

import { Grid, Menu, MenuItem, Stack, Typography } from "@mui/material";
import MenuIcon from "@mui/icons-material/Menu";
import { isMobile } from "react-device-detect";

import CustomBox from "../components/CustomBox";

function Header({ title }) {
  const pages = [
    { title: "HOME", path: "/" },
    { title: "ABOUT US", path: "/about" },
    { title: "PROJECTS", path: "/project" },
    { title: "BANTO PARTNERS", path: "/partners" },
    { title: "CONTACTS", path: "/contact" },
  ];

  return (
    <Grid
      container
      spacing={2}
      justifyContent="center"
      alignItems="center"
      height={150}
    >
      <Grid item xs={4} lg={4}>
        <CustomBox>
          <Typography
            fontSize={{
              lg: 40,
              xs: 25,
            }}
            textAlign="left"
            fontWeight={700}
          >
            BANTO
          </Typography>
        </CustomBox>
      </Grid>
      <Grid item xs={8} lg={8}>
        <CustomBox>
          {isMobile ? (
            <CustomBox
              sx={{
                textAlign: "right",
              }}
            >
              <MenuIcon />
            </CustomBox>
          ) : (
            <Stack direction="row" spacing={5}>
              {pages.map((page) =>
                title == page.title ? (
                  <CustomBox
                    component="a"
                    href={page.path}
                    borderTop="1px solid #333333"
                    borderBottom="1px solid #333333"
                  >
                    <Typography
                      fontSize={{
                        lg: 15,
                        xs: 10,
                      }}
                    >
                      {page.title}
                    </Typography>
                  </CustomBox>
                ) : (
                  <CustomBox component="a" href={page.path}>
                    <Typography
                      fontSize={{
                        lg: 15,
                        xs: 10,
                      }}
                    >
                      {page.title}
                    </Typography>
                  </CustomBox>
                )
              )}
            </Stack>
          )}
        </CustomBox>
      </Grid>
    </Grid>
  );
}

export default Header;
