                  window.alert("현재 대여중입니다. 반납후 다시 시도해주세요");
                  // props.history.push("/");
                  return;
                }
                const stationInfo = await axios.post(
                  constants.hosts.banto + "/banto2/app/store/getStationInfo",
                  {
                    stationId: sessionStorage.getItem("stationId"),
                  }
                );

                if (stationInfo.data.code !== 200) {
                  window.alert("시스템 에러: 반토 고객센터에 문의하세요");
                  // props.history.push("/");
                  return;
                }
                console.log("스테이션인포", stationInfo.data.body);
                if (stationInfo.data.body.workState === 2) {
                  window.alert("모든 배터리가 충전중에 있습니다 기다려 주세요");
                  return;
                }

                if (stationInfo.data.body.workState !== 1) {
                  window.alert(
                    "충전 스테이션이 아직 네트워크에 연결 되지 않았습니다. 확인 후 다시 시도해 주세요. (전원을 킨 후 1분정도의 시간이 걸릴 수 있습니다)"
                  );
                  // props.history.push("/");
                  return;
                }
                const rent = await axios.post(
                  constants.hosts.banto + "/banto2/app/rent/requestSimpleRent",
                  {
                    userId: sessionStorage.getItem("userId"),
                    stationId: sessionStorage.getItem("stationId"),
                    couponId: null,
                  }
                );

                console.log(rent);

                if (rent.data.code !== 200) {
                  alert(rent.data.msg);
                  props.history.push("/simple/rentfail");
                  return;
                }
                props.history.push("/simple/renting");
                return;
              }}
            >
              동의 후 대여하기
              {/* test rent battery */}
            </Button>

            <div
              style={{
                width: "100%",
                height: "300px",
              }}
              className="purchaseForWebEmptySpace"
            />
            <div
              style={{
                position: "fixed",
                left: "16px",
                right: "16px",
                bottom: "50px",
              }}
              className="purchaseForWebButtonContainer"
            ></div>
            <div>
              <Dialog
                open={open}
                onClose={handleClose}
                scroll={scroll}
                aria-labelledby="scroll-dialog-title"
                aria-describedby="scroll-dialog-description"
                fullScreen
              >
                <DialogTitle id="scroll-dialog-title">약관 내용</DialogTitle>
                <DialogContent
                  style={{ height: "100%" }}
                  dividers={scroll === "paper"}
                >
                  <DialogContentText
                    style={{ width: "100%", height: "100%" }}
                    id="scroll-dialog-description"
                    ref={descriptionElementRef}
                    tabIndex={-1}
                  >
                    {
                      <iframe
                        style={{ width: "100%", height: "100%" }}
                        src={policyUrl}
                      />
                    }
                  </DialogContentText>
                </DialogContent>
                <DialogActions>
                  <Button onClick={handleClose} color="primary">
                    확인
                  </Button>
                </DialogActions>
              </Dialog>
            </div>
          </body>
        </>
      )}
    </div>
  );
}
