import React from "react";
import axios from "axios";
import constants from "../../Constants";
import moment from "moment";

import Inquiry from "./components/Inquiry";
import DesText from "./components/DesText";
import Button from "./components/Button";
import Card from "./components/Card";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";

import { Stack, Typography } from "@mui/material";
import { Box } from "@mui/system";
import CustomCenterBox from "../../components/CustomCenterBox";

export default function App(props) {
  const [open, setOpen] = React.useState(false);
  const [usage, setUsage] = React.useState(null);
  const [policyUrl, setPolicyUrl] = React.useState(
    "https://bantoweb.xyz/policy/service_policy"
  );

  const descriptionElementRef = React.useRef(null);

  const handleOpen = () => {
    setOpen(true);
  };
  const handleClose = () => {
    setOpen(false);
  };
  const [scroll, setScroll] = React.useState("paper");

  const [stationId, setStationId] = React.useState("");
  const [returned, setReturned] = React.useState(null);
  const [rented, setRented] = React.useState(null);
  const [rentStatus, setRentStatus] = React.useState(null);

  React.useEffect(() => {
    axios
      .post(constants.hosts.banto + "/banto2/app/user/checkUserPayment", {
        userId: sessionStorage.getItem("userId"),
      })
      .then((res) => {
        if (res.data.code !== 200) {
        }
        const payment = res.data.data.payment;
        let elem = document.getElementById("payment");
        if (payment === "kakaoPay") {
          elem.innerHTML = "카카오페이";
        } else if (payment === "creditCard") {
          elem.innerHTML = "신용카드";
        } else {
        }
      });
  }, []);

  function checkMobile() {
    var varUA = navigator.userAgent.toLowerCase(); //userAgent 값 얻기

    if (varUA.indexOf("android") > -1) {
      //안드로이드
      return "android";
    } else if (
      varUA.indexOf("iphone") > -1 ||
      varUA.indexOf("ipad") > -1 ||
      varUA.indexOf("ipod") > -1
    ) {
      //IOS
      return "ios";
    } else {
      //아이폰, 안드로이드 외
      return "other";
    }
  }

  React.useEffect(() => {
    if (props.location.state !== undefined) {
      setStationId(props.location.state.stationId);
    }
  }, []);

  React.useEffect(() => {
    (async () => {
      const result = await axios.post(
        constants.hosts.banto + "/banto2/app/rent/lastRent",
        {
          userId: sessionStorage.getItem("userId"),
        }
      );

      if (result.data.code !== 200) {
        window.alert("시스템 에러: 반토 고객센터에 문의하세요");
        return;
      }
      setRentStatus(
        result.data.data.bRenting && result.data.data.bRenting.status
      );
      setReturned(
        result.data.data.bRenting && result.data.data.bRenting.returnTimeStamp
      );
      setRented(
        result.data.data.bRenting && result.data.data.bRenting.rentalTimeStamp
      );

      return;
    })();
  }, []);

  function timer() {
    setInterval(function () {
      setUsage(intervalBetween(rented));
    }, 1000);
  }
  timer();

  const intervalBetween = (rentTime) => {
    if (rentTime === null) {
      return "-:--:--";
    }
    var rentalTime = moment(rentTime);
    var now = moment();

    var diff = now.diff(rentalTime, "seconds", true);
    const formatted = moment.utc(diff * 1000).format("H:mm:ss");

    return formatted;
  };

  function leadingZeros(n, digits) {
    var zero = "";
    n = n.toString();

    if (n.length < digits) {
      for (let i = 0; i < digits - n.length; i++) zero += "0";
    }
    return zero + n;
  }
  function getTimeStamp(time) {
    var d = new Date(time);
    var s =
      leadingZeros(d.getFullYear(), 4) +
      "-" +
      leadingZeros(d.getMonth() + 1, 2) +
      "-" +
      leadingZeros(d.getDate(), 2) +
      " " +
      leadingZeros(d.getHours(), 2) +
      ":" +
      leadingZeros(d.getMinutes(), 2) +
      ":" +
      leadingZeros(d.getSeconds(), 2);

    return s;
  }

  return (
    <Box sx={{ backgroundColor: "#0B0B0C", height: "100vh" }}>
      <CustomCenterBox sx={{ pt: "20%" }}>
        <CustomCenterBox sx={{ p: 5, alignItems: "center" }}>
          <Typography color="white" fontSize={40} fontWeight={700}>
            BANTO
          </Typography>
        </CustomCenterBox>
      </CustomCenterBox>

      <CustomCenterBox>
        <Typography
          color="#00E676"
          fontFamily="Montserrat"
          fontWeight={700}
          fontSize={50}
        >
          {rentStatus === "return_payment_fail" ? "반납 결제 실패" : usage}
        </Typography>
        <Typography color="#00E676" fontWeight={300}>
          현재 이용 시간
        </Typography>
      </CustomCenterBox>

      <Box sx={{ p: 10, pl: 2, pr: 2 }}>
        <Box
          sx={{
            backgroundColor: "black",
            border: "2px solid #242427",
            boxSizing: "border-box",
            borderRadius: "15px",
          }}
        >
          <Box sx={{ p: 3 }}>
            <Stack spacing={3}>
              <Box
                sx={{
                  display: "flex",
                  flexDirection: "row",
                  justifyContent: "space-between",
                }}
              >
                <Typography color="#94949d" fontSize={14}>
                  대여 시간
                </Typography>
                <Typography color="#EAEBF1" fontWeight={600} fontSize={16}>
                  {rented && getTimeStamp(rented)}
                </Typography>
              </Box>
              <Box
                sx={{
                  display: "flex",
                  flexDirection: "row",
                  justifyContent: "space-between",
                }}
              >
                <Typography color="#94949d" fontSize={14}>
                  대여 지점
                </Typography>
                <Typography
                  color="#EAEBF1"
                  fontWeight={600}
                  fontSize={16}
                ></Typography>
              </Box>
              <Box
                sx={{
                  display: "flex",
                  flexDirection: "row",
                  justifyContent: "space-between",
                }}
              >
                <Typography color="#94949d" fontSize={14}>
                  추가 이용료 (10분/150원)
                </Typography>
                <Typography
                  color="#EAEBF1"
                  fontWeight={600}
                  fontSize={16}
                ></Typography>
              </Box>
              <Box
                sx={{
                  display: "flex",
                  flexDirection: "row",
                  justifyContent: "space-between",
                }}
              >
                <Typography color="#94949d" fontSize={14}>
                  결제 수단
                </Typography>
                <Typography
                  id="payment"
                  color="#EAEBF1"
                  fontWeight={600}
                  fontSize={16}
                ></Typography>
              </Box>
            </Stack>
          </Box>
        </Box>
      </Box>

      <Button
        callback={async () => {
          const result = await axios.post(
            constants.hosts.banto + "/banto2/app/rent/lastRent",
            {
              userId: sessionStorage.getItem("userId"),
            }
          );

          if (result.data.code !== 200) {
            window.alert("시스템 에러: 반토 고객센터에 문의하세요");
            return;
          }
          setRentStatus(
            result.data.data.bRenting && result.data.data.bRenting.status
          );
          setReturned(
            result.data.data.bRenting &&
              result.data.data.bRenting.returnTimeStamp
          );
          setRented(
            result.data.data.bRenting &&
              result.data.data.bRenting.rentalTimeStamp
          );

          if (
            result.data.data.bRenting &&
            result.data.data.bRenting.returnTimeStamp !== null
          ) {
            let msg = "반납이 완료되었습니다";

            if (result.data.data.bRenting.status === "return_payment_fail") {
              const { data } = await axios.get(
                `${constants.hosts.banto}/banto2/app/log/${result.data.data.bRenting.userId}/payment-fail/${result.data.data.bRenting.rentId}`
              );

              msg += `\n ${data.reason}`;
            }

            alert(msg);
            props.history.push("/simple/rentcomplete");
          } else {
            alert(
              `반납은 10초가량 소요될 수 있습니다.\n\n배터리를 꾸욱 눌러주세요.\n\n반납이 되지 않을시 스테이션 뒤의 전원을 껐다 켜주세요`
            );
          }

          return { code: 200 };
        }}
      >
        반납 확인
      </Button>
      <DesText
        style={{
          marginTop: "30px",
          fontSize: "16px",
          color: "#c0c1c5",
          marginLeft: "16px",
          marginRight: "16px",
          marginBottom: "5px",
        }}
      >
        안내사항
      </DesText>
      <Card>
        <DesText>{"-1일당 10시간(9,000원)만 결제 됩니다"}</DesText>
        <DesText>
          {
            "-미반납(3일) 분실비용(29,900원)이 부가 되오니 꼭 반납을 부탁드립니다."
          }
        </DesText>
      </Card>
      <Inquiry />
      <div
        style={{
          width: "100%",
          height: "300px",
        }}
        className="purchaseForWebEmptySpace"
      />

      <div
        style={{
          position: "fixed",
          left: "16px",
          right: "16px",
          bottom: "50px",
        }}
        className="purchaseForWebButtonContainer"
      ></div>
      <div>
        <Dialog
          open={open}
          onClose={handleClose}
          scroll={scroll}
          aria-labelledby="scroll-dialog-title"
          aria-describedby="scroll-dialog-description"
          fullScreen
        >
          <DialogTitle id="scroll-dialog-title">약관 내용</DialogTitle>
          <DialogContent
            style={{ height: "100%" }}
            dividers={scroll === "paper"}
          >
            <DialogContentText
              style={{ width: "100%", height: "100%" }}
              id="scroll-dialog-description"
              ref={descriptionElementRef}
              tabIndex={-1}
            >
              {
                <iframe
                  style={{ width: "100%", height: "100%" }}
                  src={policyUrl}
                />
              }
            </DialogContentText>
          </DialogContent>
          <DialogActions>
            <Button onClick={handleClose} color="primary">
              확인
            </Button>
          </DialogActions>
        </Dialog>
      </div>
    </Box>
  );
}
