import { createTheme } from "@mui/material/styles";

import NotoSansKR from "../assets/fonts/NotoSansKR-Regular.otf";
import NotoSans from "../assets/fonts/NotoSans-Regular.ttf";

const notoSansKR = {
  fontFamily: "NotoSansKR",
  fontStyle: "normal",
  fontWeight: 100,
  src: `url(${NotoSansKR})`,
};

const notoSans = {
  fontFamily: "NotoSans",
  fontStyle: "normal",
  fontWeight: 100,
  src: `url(${NotoSans})`,
};

const theme = createTheme({
  typography: {
    fontFamily: "NotoSansKR",
  },
  components: {
    MuiCssBaseline: {
      styleOverrides: {
        "@global": {
          "@font-face": [notoSansKR],
        },
      },
    },
  },
});

export default theme;
