import React, { useState } from "react";

import { Button, Grid, TextField, Typography } from "@mui/material";
import { Box, styled } from "@mui/system";
import ContactUsImage from "../../assets/img/home/phone_battery1.png";
import ArrowRightAltIcon from "@mui/icons-material/ArrowRightAlt";

import { isMobile } from "react-device-detect";

const nodemailer = require("nodemailer");
const mailAddress = "jaylee@banto.io";
const mailPassword = "";

const StyledTextField = styled(TextField)(({ theme }) => ({
  div: {
    borderRadius: 0,
    border: "0px",
  },
}));

function ContactUs() {
  const [name, setName] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [email, setEmail] = useState("");
  const [interested, setInterested] = useState("");
  const [message, setMessage] = useState("");

  const sendMail = () => {
    const transporter = nodemailer.createTransport({
      service: "gmail", // 메일 보내는 곳
      prot: 587,
      host: "smtp.gmlail.com",
      secure: false,
      requireTLS: true,
      auth: {
        user: "", // 보내는 메일의 주소
        pass: "", // 보내는 메일의 비밀번호
      },
    });
    // 메일 옵션
    const mailOptions = {
      from: "", // 보내는 메일의 주소
      to: "", // 수신할 이메일
      subject: "문의 메일", // 메일 제목
      text: `
    Name : ${name}
    Phone Number : ${phoneNumber}
    E-Mail : ${email}
    Interested in : ${interested}
    Message : ${message}
      `, // 메일 내용
    };

    // 메일 발송
    transporter.sendMail(mailOptions, function (error, info) {
      if (error) {
        console.log(error);
      } else {
        console.log("Email sent: " + info.response);
        alert("메일이 전송됐습니다.");
      }
    });
  };

  return (
    <Grid
      container
      justifyContent="center"
      alignItems="center"
      p={4}
      sx={{ marginTop: { xs: 10, lg: 30 }, backgroundColor: "#E9E9EB" }}
    >
      <Grid item xs={12} lg={6}>
        <Box sx={{ pl: { lg: 7, xs: 0 }, pr: { lg: 4, xs: 0 } }}>
          <Typography
            fontSize={{ lg: 54, xs: 30 }}
            letterSpacing="0em"
            color="#BDBDBD"
            textAlign="left"
          >
            Contact Us
          </Typography>
          <StyledTextField
            fullWidth
            label="Name"
            margin="normal"
            variant="filled"
            value={name}
            onChange={(e) => {
              setName(e.target.value);
            }}
          />
          <StyledTextField
            fullWidth
            required
            label="Phone Number"
            type="number"
            margin="normal"
            variant="filled"
            value={phoneNumber}
            onChange={(e) => {
              setPhoneNumber(e.target.value);
            }}
          />
          <StyledTextField
            fullWidth
            required
            label="E-mail"
            type="email"
            margin="normal"
            variant="filled"
            value={email}
            onChange={(e) => {
              setEmail(e.target.value);
            }}
          />
          <StyledTextField
            fullWidth
            label="Interested In"
            margin="normal"
            variant="filled"
            value={interested}
            onChange={(e) => {
              setInterested(e.target.value);
            }}
          />
          <StyledTextField
            fullWidth
            required
            multiline
            rows={4}
            label="Message"
            margin="normal"
            variant="filled"
            value={message}
            onChange={(e) => {
              setMessage(e.target.value);
            }}
          />

          <Button
            sx={{
              color: "white",
              width: isMobile ? "150px" : "222px",
              height: isMobile ? "50px" : "71px",
              background: "#333333",
              borderRadius: 0,
            }}
            onClick={sendMail}
          >
            SEND EMAIL
            <ArrowRightAltIcon />
          </Button>
        </Box>
      </Grid>
      <Grid item xs={12} lg={6}>
        <img src={ContactUsImage} alt="" width="100%" />
      </Grid>
    </Grid>
  );
}

export default ContactUs;
