import React from "react";

import { Grid, Menu, MenuItem, Stack, Typography } from "@mui/material";
import MenuIcon from "@mui/icons-material/Menu";
import { isMobile } from "react-device-detect";

import CustomBox from "../components/CustomBox";

function Header({ title }) {
  const pages = ["HOME", "ABOUT US", "PROJECTS", "BANTO PARTNERS", "CONTACTS"];

  return (
    <Grid container justifyContent="center" alignItems="center" m={4}>
      <Grid item xs={3} lg={4}>
        <CustomBox>
          <Typography
            fontSize={{
              lg: 40,
              xs: 25,
            }}
            fontWeight={700}
          >
            BANTO
          </Typography>
        </CustomBox>
      </Grid>
      <Grid item xs={9} lg={8}>
        <CustomBox>
          {isMobile ? (
            <CustomBox alignItems="right">
              <MenuIcon />
            </CustomBox>
          ) : (
            <Stack direction="row" spacing={5}>
              {pages.map((page) =>
                title == page ? (
                  <CustomBox
                    borderTop="1px solid #333333"
                    borderBottom="1px solid #333333"
                  >
                    <Typography
                      fontSize={{
                        lg: 15,
                        xs: 10,
                      }}
                    >
                      {page}
                    </Typography>
                  </CustomBox>
                ) : (
                  <CustomBox>
                    <Typography
                      fontSize={{
                        lg: 15,
                        xs: 10,
                      }}
                    >
                      {page}
                    </Typography>
                  </CustomBox>
                )
              )}
            </Stack>
          )}
        </CustomBox>
      </Grid>
    </Grid>
  );
}

export default Header;
