import React, { useCallback, useState } from "react";

import Page from "../../components/Page";
import IntroduceText from "../../components/IntroduceText";

import { GoogleMap, useJsApiLoader, Marker } from "@react-google-maps/api";

import { Grid, CircularProgress } from "@mui/material";

const googleMapsApiKey = "AIzaSyBRsdmnSRwU4va6sYZEs7pyi2MZ9XhgvPw";

function Index() {
  const [map, setMap] = useState(null);

  const { isLoaded } = useJsApiLoader({
    id: "google-map-script",
    googleMapsApiKey: googleMapsApiKey,
  });

  const onLoad = useCallback((map) => {
    const bounds = new window.google.maps.LatLngBounds();
    map.fitBounds(bounds);
    setMap(map);
  }, []);

  const onUnmount = useCallback((map) => {
    setMap(null);
  }, []);

  const mapOptions = {
    fullscreenControl: false,
    panControl: false,
    zoomControl: false,
    mapTypeControl: false,
    streetViewControl: false,
    minZoom: 11,
    maxZoom: 20,
  };

  const center = {
    lat: 4507088.9938378,
    lng: 14141177.9109503,
  };
  return (
    <Page title="CONTACTS">
      <Grid
        container
        spacing={2}
        justifyContent="center"
        alignItems="center"
        mt={3}
      >
        <Grid item xs={12} lg={6}>
          <IntroduceText firstLine="Contact" secondLine="Information" />
        </Grid>
        <Grid item xs={12} lg={6}>
          isLoaded ?
          <GoogleMap
            mapContainerStyle={{ width: "100%", height: "100%" }}
            center={center}
            zoom={13}
            onLoad={onLoad}
            onUnmount={onUnmount}
          >
            <Marker
              key={1}
              icon="/images/icon/current_location.png"
              position={center}
            />
          </GoogleMap>
          : <></>
        </Grid>
      </Grid>
    </Page>
  );
}

export default Index;
