import { MenuItem } from "@material-ui/core";
import {
  InputLabel,
  OutlinedInput,
  Stack,
  TextField,
  FormControl,
  Input,
} from "@mui/material";
import { Box } from "@mui/system";
import React, { useState } from "react";
import Cards from "react-credit-cards";
import "react-credit-cards/es/styles-compiled.css";

function CardInputForm() {
  const [number, setNumber] = useState("");
  const [name, setName] = useState("");
  const [month, setMonth] = useState("");
  const [expiry, setExpiry] = useState("");
  const [cvc, setCvc] = useState("");
  const [focus, setFocus] = useState("");

  const handleDate = (e) => {
    setMonth(e.target.value);
    setExpiry(e.target.value);
  };

  const handleExpiry = (e) => {
    setExpiry(month.concat(e.target.value));
  };

  const monthArr = [
    "01",
    "02",
    "03",
    "04",
    "05",
    "06",
    "07",
    "08",
    "09",
    "10",
    "11",
    "12",
  ];

  const yearArr = [22, 23, 24, 25, 26, 27, 28, 29, 30];

  return (
    <>
      <Box sx={{ pt: 8 }}>
        <Cards
          locale={{ valid: "유효기간" }}
          placeholders={{ name: "별칭" }}
          number={number}
          name={name}
          expiry={expiry}
          cvc={cvc}
          focused={focus}
        />
      </Box>

      <Box sx={{ p: 4 }}>
        <div className="card">
          <form className="payment-form">
            <div className="form-group">
              <label htmlFor="cardNumber" className="card-label">
                Card Number
              </label>
              <input
                type="text"
                onChange={validateInput}
                value={number}
                onKeyDown={removeSpecial}
                onPaste={(e) => e.preventDefault()}
                onKeyPress={addSpace}
                onFocus={handleFocus}
                name="number"
                maxLength="19"
                id="cardNumber"
                className="form-control form-control-lg"
              />
            </div>
            <div className="form-group">
              <label htmlFor="cardHolder" className="card-label">
                Card holder
              </label>
              <input
                type="text"
                name="name"
                spellCheck="false"
                value={name}
                maxLength="20"
                autoComplete="off"
                onPaste={(e) => e.preventDefault()}
                onChange={handleInputChange}
                onFocus={handleFocus}
                id="cardHolder"
                className="form-control form-control-lg"
              />
            </div>
            <div className="date-cvv-box">
              <div className="expiry-class">
                <div className="form-group card-month ">
                  <label htmlFor="cardMonth" className="card-label">
                    Expiration Date
                  </label>

                  <select
                    id="cardMonth"
                    data-ref="cardDate"
                    value={expiry}
                    name="expiry"
                    onChange={handleInputChange}
                    onFocus={handleFocus}
                    className="form-control form-control-lg"
                  >
                    <option value="" defaultChecked="true">
                      Month
                    </option>
                    <option value="01">01</option>
                    <option value="02">02</option>
                    <option value="03">03</option>
                    <option value="04">04</option>
                    <option value="05">05</option>
                    <option value="06">06</option>
                    <option value="07">07</option>
                    <option value="08">08</option>
                    <option value="09">09</option>
                    <option value="10">10</option>
                    <option value="11">11</option>
                    <option value="12">12</option>
                  </select>
                </div>
                <div className="form-group card-year">
                  <select
                    id="cardYear"
                    data-ref="cardDate"
                    value={expiryyear}
                    name="expiryyear"
                    onChange={handleInputChange}
                    onFocus={handleFocus}
                    className="form-control form-control-lg"
                  >
                    <option value="" defaultChecked="true">
                      Year
                    </option>
                    <option value="2020">2020</option>
                    <option value="2021">2021</option>
                    <option value="2022">2022</option>
                    <option value="2023">2023</option>
                    <option value="2024">2024</option>
                    <option value="2025">2025</option>
                    <option value="2026">2026</option>
                    <option value="2027">2027</option>
                    <option value="2028">2028</option>
                    <option value="2029">2029</option>
                    <option value="2030">2030</option>
                    <option value="2031">2031</option>
                  </select>
                </div>
              </div>

              <div className="cvv-class form-group">
                <label htmlFor="cvv" className="card-label cvv-label">
                  CVV
                </label>
                <input
                  type="number"
                  onChange={validateInput}
                  onKeyDown={removeSpecial}
                  onPaste={(e) => e.preventDefault()}
                  onFocus={handleFocus}
                  name="cvc"
                  id="cvv"
                  value={cvc}
                  className="form-control form-control-lg "
                  maxLength="4"
                />
              </div>
            </div>

            <button
              className="btn btn-primary btn-lg btn-block"
              onClick={submit}
            >
              Submit
            </button>
          </form>
        </div>
      </Box>
    </>
  );
}

export default CardInputForm;
