import React, { useEffect } from "react";
import { makeStyles } from "@material-ui/styles";
import { Link, Redirect } from "react-router-dom";
import MenuSharpIcon from "@material-ui/icons/MenuSharp";
import CloseSharpIcon from "@material-ui/icons/CloseSharp";
import queryString from "query-string";

const useStyles = makeStyles((theme) => ({
  appHeader: {
    display: "flex",
    border: "0",
    width: "100%",
    position: "sticky",
    top: "0px",
    backgroundColor: "white",
    justifyContent: "space-between",
    alignItems: "center",
    flexDirection: "row",
    flexFlow: "row nowrap",
    zIndex: "unset",
    padding: "10px",
    paddingLeft: "30px",
    paddingRight: "30px",
    boxSizing: "border-box",
  },
  appHeader__logo: { width: "96px", height: "21px" },
  appHeader__menu: {
    "& li": {
      padding: "10px 0px 10px 30px;",
    },
    display: "flex",
    justifyContent: "space-around",
    listStyle: "none",
  },
  introSection: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  appHeaderMobileMenu: { display: "none" },
  hand_img: { left: "15%", width: "540px", height: "auto" },
  // leftTexts: { height: "auto" },
  leftText: {
    fontFamily: "Noto Sans KR",
    fontWeight: "900",
    letterSpacing: "0.3px",
    fontSize: "40px",
    lineHeight: "58px",
  },
  iconContainer: { display: "flex", justifyContent: "start", height: "50%" },
  appstoreIcon: {
    marginTop: "21px",
    width: "105px",
    height: "30px",
    marginRight: "15px",
    borderRadius: "7px",
  },
  googleStoreIcon: {
    marginTop: "21px",
    width: "105px",
    height: "30px",
    borderRadius: "7px",
  },
  appUsageContainer: {
    margin: "0px auto",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "black",
  },
  appUsage_texts: {
    color: "#4f3f3f",
    marginRight: "12%",
    "& li": {
      padding: "20px 00px 20px 00px;",
      clear: "both",
      diplay: "flex",
      alignItems: "baseline",
      whiteSpace: "nowrap",
      fontFamily: "Noto Sans KR",
      fontStyle: "normal",
      fontWeight: "500",
      fontSize: "24px",
    },
  },
  appUsageText_deactive: {
    "&:hover": {
      color: "grey",
      cursor: "pointer",
    },
  },
  appUsageText_active: {
    color: "white",
    "&:hover": {
      cursor: "pointer",
    },
  },
  appUsageImg: {
    marginTop: "202px",
    marginBottom: "201px",
    width: "300px",
    height: "auto",

    transition: [".5s", "ease"],
  },
  thunder_active: {
    width: "10px",
    height: "19px",
    verticalAlign: "middle",
    marginRight: "10px",
  },
  thunder_deactive: {
    width: "10px",
    height: "19px",
    verticalAlign: "middle",
    marginRight: "10px",
    visibility: "hidden",
  },
  partnersContainer: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  partnerLogoImg: {
    width: "394px",
    height: "62px",
    marginTop: "318px",
    marginBottom: "10px",
  },
  partnerText: {
    fontFamily: "Noto Sans KR",
    fontStyle: "normal",
    fontWeight: "900",
    fontSize: "50px",
    lineHeight: "72px",
  },
  partnerButton: {
    width: "300px",
    height: "84px",
    border: "4px solid #000000",
    backgroundColor: "transparent",
    fontFamily: "Noto Sans KR",
    fontStyle: "normal",
    fontWeight: "900",
    fontSize: "20px",
    lineHeight: "29px",
    marginTop: "60px",
    marginBottom: "318px",
  },
  partnershipContainer: {
    backgroundColor: "black",
    display: "flex",
    alignItems: "center",
    flexDirection: "column",
  },
  partnershipTitle: {
    marginTop: "271px",
    fontFamily: "Noto Sans KR",
    fontStyle: "normal",
    fontWeight: "900",
    fontSize: "50px",
    color: "#F0F0F0",
    marginBottom: "60px",
  },
  partnershipGrid: {
    display: "flex",
    marginBottom: "210px",
    flexWrap: "wrap",
    justifyContent: "space-around",
    alignContent: "space-around",
  },
  partnerInsideGrid: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    padding: "16px",
  },
  footerContainer: {
    display: "flex",
    flexDirection: "column",
    backgroundColor: "black",
    alignItems: "center",
  },
  footerLogo: {
    width: "261px",
    height: "auto",
    marginTop: "60px",
    marginBottom: "30px",
  },

  footerDivider: {
    margin: "0px",
    color: "white",
    width: "100%",
    border: "1px solid #4F4F4F",
    boxSizing: "border-box",
  },
  footerTexts: {
    color: "#4f4f4f",
    lineHeight: "30px",
    textAlign: "center",
    marginBottom: "60px",
  },
  a: {
    textDecoration: "none",
    color: "black",
  },
  ab: {
    textDecoration: "none",
    color: "#4f4f4f",
  },
  "@media (max-width: 768px)": {
    appHeader: { padding: "10px" },
    appHeader__menu: {
      display: "none",
    },
    appHeaderMobileMenu: {
      display: "block",
      paddingRight: "10px",
      textDecoration: "none",
      color: "black",
    },

    introSection: {
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      flexDirection: "column-reverse",
    },
    hand_img: {
      display: "flex",
      alignSelf: "center",
      marginLeft: "5%",
      marginRight: "150px",
      width: "330px",
    },
    leftTexts: {
      marginTop: "35px",
    },
    leftText: {
      textAlign: "center",
    },
    iconContainer: { marginTop: "10px" },
    appstoreIcon: { width: "150px", height: "auto", borderRadius: "7px" },
    googleStoreIcon: { width: "150px", height: "auto", borderRadius: "7px" },
    appUsageContainer: {
      margin: "0px auto",
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      backgroundColor: "black",
      flexDirection: "column-reverse",
    },
    appUsageImg: { margin: "60px 10px auto", width: "200px", height: "auto" },
    appUsage_texts: {
      display: "flex",
      justifyContent: "space-between",
      alignContent: "space-around",
      margin: "30px",
      width: "90%",
      "& li": {
        padding: "15px",
        fontSize: "18px",
      },
    },
    partnerLogoImg: {
      width: "230px",
      height: "auto",
      marginTop: "60px",
      marginBottom: "45px",
    },
    partnerText: {
      fontFamily: "Noto Sans KR",
      fontStyle: "normal",
      fontWeight: "900",
      fontSize: "40px",
      textAlign: "center",
      lineHeight: "45px",
