import { createTheme } from "@mui/material/styles";

import NotoSansKR from "../assets/fonts/NotoSansKR-Regular.otf";
import NotoSans from "../assets/fonts/NotoSans-Regular.ttf";

const notoSansKR = {
  fontFamily: "NotoSansKR",
  fontWeight: 400,
  src: `url(${NotoSansKR})`,
};

const notoSans = {
  fontFamily: "NotoSans",
  fontWeight: 400,
  src: `url(${NotoSans})`,
};

const theme = createTheme({
