import { createTheme } from "@mui/material/styles";

import NotoSansKR from "../assets/fonts/NotoSansKR-Regular.otf";

const notoSansKR = {
  fontFamily: "NotoSansKR",
  fontStyle: "normal",
  fontWeight: 100,
  src: `url(${NotoSansKR})`,
};

const theme = createTheme({
  components: {
    MuiCssBaseline: {
      styleOverrides: {
        "@global": {
          "@font-face": [notoSansKR],
        },
      },
    },
  },
});

export default theme;
