import React from "react";

import { Grid, Stack, ImageList, ImageListItem } from "@mui/material";
import IntroduceText from "../../components/IntroduceText";
import CustomCenterBox from "../../components/CustomCenterBox";

import photoGallery1 from "../../assets/img/about/photo_gallery1.png";
import photoGallery2 from "../../assets/img/about/photo_gallery2.png";
import photoGallery3 from "../../assets/img/about/photo_gallery3.png";
import photoGallery4 from "../../assets/img/about/photo_gallery4.png";
import photoGallery5 from "../../assets/img/about/photo_gallery5.png";

function srcset(image, size, rows = 1, cols = 1) {
  return {
    src: `${image}?w=${size * cols}&h=${size * rows}&fit=crop&auto=format`,
    srcSet: `${image}?w=${size * cols}&h=${
      size * rows
    }&fit=crop&auto=format&dpr=2 2x`,
  };
}

function PhotoGallery() {
  return (
    <Grid
      container
      spacing={2}
      justifyContent="center"
      alignItems="center"
      mt={3}
    >
      <Grid item xs={12} lg={12}>
        <IntroduceText firstLine="Photo" secondLine="Gallery" />
      </Grid>

      <Grid item xs={12} lg={12}>
        <ImageList
          sx={{ width: 500, height: 450 }}
          variant="quilted"
          cols={4}
          rowHeight={121}
        >
          <ImageListItem key={photoGallery1} cols={1} rows={1}>
            <img
              {...srcset(item.img, 121, item.rows, item.cols)}
              alt={item.title}
              loading="lazy"
            />
          </ImageListItem>
        </ImageList>
        {/* <Stack
          direction="row"
          justifyContent="center"
          alignItems="center"
          spacing={1}
        >
          <CustomCenterBox>
            <img src={photoGallery1} alt="" width="100%" />
          </CustomCenterBox>
          <CustomCenterBox>
            <img src={photoGallery2} alt="" width="100%" />
          </CustomCenterBox>
          <CustomCenterBox>
            <img src={photoGallery3} alt="" width="100%" />
          </CustomCenterBox>
          <CustomCenterBox>
            <img src={photoGallery4} alt="" width="100%" />
          </CustomCenterBox>
          <CustomCenterBox>
            <img src={photoGallery5} alt="" width="100%" />
          </CustomCenterBox>
        </Stack> */}
      </Grid>
    </Grid>
  );
}

export default PhotoGallery;
